mapper.select:
select
    {% if isCount == 1 %}
        *
    {% else  %}
        count(*)
    {% endif %}
from sys_config
where 1 = 1
    {% if param.ConfigType != -1 %}
        and config_type = {{param.ConfigType}}
    {% endif %}
    {% if param.ConfigName != "" %}
        and config_name like concat('%', {{param.ConfigName}}, '%')
    {% endif %}
    {% if param.ConfigKey != "" %}
        and config_key like concat('%', {{param.ConfigKey}}, '%')
    {% endif %}
    order by create_time
    {% if isCount == 1 %}
        limit {{param.Page}}, {{param.Offset}}
    {% endif %}
